package com.zlkj.ssm.shop.front.service.impl;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.front.dao.EmailNotifyProductDao;import com.zlkj.ssm.shop.front.entity.EmailNotifyProduct;import com.zlkj.ssm.shop.front.service.EmailNotifyProductService;import javax.annotation.Resource;@Transactional@Servicepublic class EmailNotifyProductServiceImpl extends		ServersManager<EmailNotifyProduct, EmailNotifyProductDao> implements EmailNotifyProductService {    @Resource    @Override    public void setDao(EmailNotifyProductDao emailNotifyProductDao) {        this.dao = emailNotifyProductDao;    }    @Override	public int selectCount(EmailNotifyProduct ep) {		return dao.selectCount(ep);	}}