package com.zlkj.ssm.shop.front.service.impl;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.front.dao.EmailDao;import com.zlkj.ssm.shop.front.entity.Email;import com.zlkj.ssm.shop.front.service.EmailService;import javax.annotation.Resource;@Transactional@Servicepublic class EmailServiceImpl extends ServersManager<Email, EmailDao> implements		EmailService {    @Resource    @Override    public void setDao(EmailDao emailDao) {        this.dao = emailDao;    }    @Override	public void updateEmailInvalidWhenReg(Email email) {		dao.updateEmailInvalidWhenReg(email);	}}