package com.zlkj.ssm.shop.front.service.impl;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.front.dao.FavoriteDao;import com.zlkj.ssm.shop.front.entity.Favorite;import com.zlkj.ssm.shop.front.service.FavoriteService;import javax.annotation.Resource;@Transactional@Servicepublic class FavoriteServiceImpl extends ServersManager<Favorite, FavoriteDao> implements		FavoriteService {    @Resource    @Override    public void setDao(FavoriteDao favoriteDao) {        this.dao = favoriteDao;    }    @Override	public int selectCount(Favorite favorite) {		return dao.selectCount(favorite);	}}