package com.zlkj.ssm.shop.front.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.front.dao.QuestionnaireDao;import com.zlkj.ssm.shop.front.entity.Questionnaire;import com.zlkj.ssm.shop.front.service.QuestionnaireService;@Transactional@Servicepublic class QuestionnaireServiceImpl extends ServersManager<Questionnaire, QuestionnaireDao>		implements QuestionnaireService {    @Autowired    @Override    public void setDao(QuestionnaireDao questionnaireDao) {        this.dao = questionnaireDao;    }	/**	 * 新增问卷	 */	@Override	public int insert(Questionnaire e) {		if(e==null){			throw new NullPointerException("e is null");		}						return super.insert(e);	}	/**	 * 修改问卷	 */	@Override	public int update(Questionnaire e) {				return super.update(e);	}}