package com.zlkj.ssm.shop.manage.entity;import java.io.Serializable;public class Orderdetail extends com.zlkj.ssm.shop.entity.common.Orderdetail		implements Serializable {	private static final long serialVersionUID = 1L;	private Product product;	public void clear() {		super.clear();		if (product != null) {			product.clear();			product = null;		}	}	public Product getProduct() {		return product;	}	public void setProduct(Product product) {		this.product = product;	}}