package com.zlkj.ssm.shop.manage.service.impl;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.manage.dao.AdminAccountDao;import com.zlkj.ssm.shop.manage.entity.Account;import com.zlkj.ssm.shop.manage.service.AdminAccountService;import javax.annotation.Resource;@Transactional@Servicepublic class AdminAccountServiceImpl extends ServersManager<Account, AdminAccountDao> implements		AdminAccountService {    @Resource    @Override    public void setDao(AdminAccountDao accountDao) {        this.dao = accountDao;    }	/**	 * 批量删除	 * 	 * @param ids	 * @return	 */	public int deletes(String[] ids) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("id不能全为空！");		}				for (int i = 0; i < ids.length; i++) {			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("id不能为空！");			}			dao.deleteById(Integer.parseInt(ids[i]));		}		return 0;	}	}