package com.zlkj.ssm.shop.manage.service.impl;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.manage.dao.AdminAttributeLinkDao;import com.zlkj.ssm.shop.manage.entity.AttributeLink;import com.zlkj.ssm.shop.manage.service.AdminAttributeLinkService;import javax.annotation.Resource;@Transactional@Servicepublic class AdminAttributeLinkServiceImpl extends ServersManager<AttributeLink, AdminAttributeLinkDao>		implements AdminAttributeLinkService {    @Resource    @Override    public void setDao(AdminAttributeLinkDao attribute_linkDao) {        this.dao = attribute_linkDao;    }    @Override	public int deleteByCondition(AttributeLink e) {		return dao.deleteByCondition(e);	}}