package com.zlkj.ssm.shop.manage.service.impl;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.manage.dao.AdminCommentDao;import com.zlkj.ssm.shop.manage.entity.Comment;import com.zlkj.ssm.shop.manage.service.AdminCommentService;import javax.annotation.Resource;@Transactional@Servicepublic class AdminCommentServiceImpl extends ServersManager<Comment, AdminCommentDao> implements		AdminCommentService {    @Resource    @Override    public void setDao(AdminCommentDao commentDao) {        this.dao = commentDao;    }	@Override	public void updateStatus(String[] ids, String status) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("评论ID不能为空！");		}		for (int i = 0; i < ids.length; i++) {			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("评论ID不能为空！");			}						Comment c = new Comment();			c.setId(ids[i]);			c.setStatus(status);			dao.updateStatus(c);		}			}	@Override	public int selectNotReplyCount() {		return dao.selectNotReplyCount();	}}