package com.zlkj.ssm.shop.manage.service.impl;import java.util.List;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.manage.dao.AdminOrderdetailDao;import com.zlkj.ssm.shop.manage.entity.Orderdetail;import com.zlkj.ssm.shop.manage.entity.ReportInfo;import com.zlkj.ssm.shop.manage.service.AdminOrderdetailService;import javax.annotation.Resource;@Transactional@Servicepublic class AdminOrderdetailServiceImpl extends ServersManager<Orderdetail, AdminOrderdetailDao>		implements AdminOrderdetailService {    @Resource    @Override    public void setDao(AdminOrderdetailDao orderdetailDao) {        this.dao = orderdetailDao;    }	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return dao.reportProductSales(orderdetail);	}}