package com.zlkj.ssm.shop.manage.service.impl;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zlkj.ssm.shop.core.ServersManager;import com.zlkj.ssm.shop.manage.dao.AdminQuestionnaireDao;import com.zlkj.ssm.shop.manage.entity.Questionnaire;import com.zlkj.ssm.shop.manage.service.AdminQuestionnaireService;import javax.annotation.Resource;@Transactional@Servicepublic class AdminQuestionnaireServiceImpl extends ServersManager<Questionnaire, AdminQuestionnaireDao>		implements AdminQuestionnaireService {    @Resource    @Override    public void setDao(AdminQuestionnaireDao questionnaireDao) {        this.dao = questionnaireDao;    }	/**	 * 新增问卷	 */	@Override	public int insert(Questionnaire e) {		if(e==null){			throw new NullPointerException("e is null");		}		return super.insert(e);	}	/**	 * 修改问卷	 */	@Override	public int update(Questionnaire e) {				return super.update(e);	}	@Override	public void changeStatus(String[] ids, String questionnaireStatusY) {		if(ids==null || ids.length==0){			return;		}				for(int i=0;i<ids.length;i++){			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("id不能为空！");			}						Questionnaire e = new Questionnaire();			e.setId(ids[i]);			e.setStatus(questionnaireStatusY);			dao.update(e);		}			}}