package com.zlkj.ssm.shop.web.controller.front;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import com.zlkj.ssm.shop.core.Services;import com.zlkj.ssm.shop.front.entity.QuestionnaireResult;import com.zlkj.ssm.shop.front.service.QuestionnaireResultService;/** * 问卷结果 * @author huangf * */@Controller("frontQuestionnaireResult")@RequestMapping("questionnaireResult")public class QuestionnaireResultAction extends FrontBaseController<QuestionnaireResult> {	@Autowired	private QuestionnaireResultService questionnaireResultService;	public QuestionnaireResultService getQuestionnaireResultService() {		return questionnaireResultService;	}	public void setQuestionnaireResultService(			QuestionnaireResultService questionnaireResultService) {		this.questionnaireResultService = questionnaireResultService;	}	@Override	public Services<QuestionnaireResult> getService() {		return questionnaireResultService;	}}