package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import com.zlkj.ssm.shop.core.ManageContainer;import com.zlkj.ssm.shop.core.page.PagerModel;import com.zlkj.ssm.shop.exception.NotThisMethod;import com.zlkj.ssm.shop.manage.entity.AccountRank;import com.zlkj.ssm.shop.manage.service.AdminAccountRankService;import com.zlkj.ssm.shop.web.controller.BaseController;import javax.servlet.http.HttpServletRequest;/** * 会员等级 * */@Controller@RequestMapping("/manage/accountRank/")public class AdminAccountRankController extends BaseController<AccountRank> {    @Autowired	private AdminAccountRankService accountRankService;    private static final String page_toList = "/manage/accountRank/accountRankList";    private static final String page_toEdit = "/manage/accountRank/accountRankEdit";    private static final String page_toAdd = "/manage/accountRank/accountRankEdit";    private AdminAccountRankController(){        super.page_toList = page_toList;        super.page_toAdd = page_toAdd;        super.page_toEdit = page_toEdit;    }    @Override	public AdminAccountRankService getService() {		return accountRankService;	}    @Override	protected void selectListAfter(PagerModel pager) {		pager.setPagerUrl("selectList");	}	public void setAccountRankService(AdminAccountRankService accountRankService) {		this.accountRankService = accountRankService;	}    @Override	public void insertAfter(AccountRank e) {		e.clear();	}	/**	 * 不支持此方法	 */	@Override	public String insert(HttpServletRequest request, @ModelAttribute("e") AccountRank e, RedirectAttributes flushAttrs) throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}	/**	 * 不支持此方法	 */	@Override	public String deletes(HttpServletRequest request, String[] ids, @ModelAttribute("e") AccountRank e, RedirectAttributes flushAttrs) throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}}