package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import com.zlkj.ssm.shop.manage.entity.Email;import com.zlkj.ssm.shop.manage.service.AdminEmailService;import com.zlkj.ssm.shop.web.controller.BaseController;/** * 发送邮件列表 */@Controller@RequestMapping("/manage/email/")public class AdminEmailController extends BaseController<Email> {	@Autowired	private AdminEmailService emailService;	private static final String page_toList = "/manage/email/emailList";	private AdminEmailController() {		super.page_toList = page_toList;		super.page_toAdd = null;		super.page_toEdit = null;	}	@Override	public AdminEmailService getService() {		return emailService;	}	public void setEmailService(AdminEmailService emailService) {		this.emailService = emailService;	}}