package com.zlkj.ssm.shop.web.controller.manage;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import com.zlkj.ssm.shop.manage.entity.EmailNotifyProduct;import com.zlkj.ssm.shop.manage.service.AdminEmailNotifyProductService;import com.zlkj.ssm.shop.web.controller.BaseController;import java.io.IOException;/** * 到货通知 */@Controller@RequestMapping("/manage/emailNotifyProduct/")public class AdminEmailNotifyProductController extends BaseController<EmailNotifyProduct> {	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(AdminEmailNotifyProductController.class);	@Autowired	private AdminEmailNotifyProductService emailNotifyProductService;	private static final String page_toList = "/manage/emailNotifyProduct/emailNotifyProductList";	private AdminEmailNotifyProductController() {		super.page_toList = page_toList;		super.page_toAdd = null;		super.page_toEdit = null;	}	@Override	public AdminEmailNotifyProductService getService() {		return emailNotifyProductService;	}	public void setEmailNotifyProductService(			AdminEmailNotifyProductService emailNotifyProductService) {		this.emailNotifyProductService = emailNotifyProductService;	}	@RequestMapping("autoNotify")	@ResponseBody	public String autoNotify(EmailNotifyProduct e) throws IOException{		logger.error("autoNotify...");		emailNotifyProductService.autoNotify();		return "success";	}}