package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import com.zlkj.ssm.shop.core.page.PagerModel;import com.zlkj.ssm.shop.manage.entity.Express;import com.zlkj.ssm.shop.manage.service.AdminExpressService;import com.zlkj.ssm.shop.web.controller.BaseController;/** * 配送方式管理 */@Controller@RequestMapping("/manage/express/")public class AdminExpressController extends BaseController<Express> {    @Autowired    private AdminExpressService expressService;    public AdminExpressController() {        super.page_toList = "/manage/express/expressList";        super.page_toEdit = "/manage/express/expressEdit";        super.page_toAdd = "/manage/express/expressEdit";    }    @Override    public AdminExpressService getService() {        return expressService;    }    @Override    protected void selectListAfter(PagerModel pager) {        pager.setPagerUrl("selectList");    }    @Override    public void insertAfter(Express e) {        e.clear();    }    public void setExpressService(AdminExpressService expressService) {        this.expressService = expressService;    }}