package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import com.zlkj.ssm.shop.manage.entity.Hotquery;import com.zlkj.ssm.shop.manage.service.AdminHotqueryService;import com.zlkj.ssm.shop.web.controller.BaseController;/** * 热门查询管理 */@Controller@RequestMapping("/manage/hotquery/")public class AdminHotqueryController extends BaseController<Hotquery> {	@Autowired	private AdminHotqueryService hotqueryService;	private static final String page_toList = "/manage/hotquery/hotqueryList";	private static final String page_toEdit = "/manage/hotquery/hotqueryEdit";	private static final String page_toAdd = "/manage/hotquery/hotqueryEdit";	private AdminHotqueryController() {		super.page_toList = page_toList;		super.page_toAdd = page_toAdd;		super.page_toEdit = page_toEdit;	}	@Override	public AdminHotqueryService getService() {		return hotqueryService;	}	public void setHotqueryService(AdminHotqueryService hotqueryService) {		this.hotqueryService = hotqueryService;	}}