package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import com.zlkj.ssm.shop.core.page.PagerModel;import com.zlkj.ssm.shop.manage.entity.Navigation;import com.zlkj.ssm.shop.manage.service.AdminNavigationService;import com.zlkj.ssm.shop.web.controller.BaseController;import javax.servlet.http.HttpServletRequest;/** * 友情链接管理 */@Controller@RequestMapping("/manage/navigation/")public class AdminNavigationController extends BaseController<Navigation> {	@Autowired	private AdminNavigationService navigationService;	private static final String page_toList = "/manage/navigation/navigationList";	private static final String page_toEdit = "/manage/navigation/navigationEdit";	private static final String page_toAdd = "/manage/navigation/navigationEdit";	private AdminNavigationController() {		super.page_toList = page_toList;		super.page_toAdd = page_toAdd;		super.page_toEdit = page_toEdit;	}	@Override	public AdminNavigationService getService() {		return navigationService;	}	@Override	protected void selectListAfter(PagerModel pager) {		pager.setPagerUrl("selectList");	}	public void setNavigationService(AdminNavigationService navigationService) {		this.navigationService = navigationService;	}	@Override	public void insertAfter(Navigation e) {		e.clear();	}	@RequestMapping(value = "insert", method = RequestMethod.POST)	@Override	public String insert(HttpServletRequest request, Navigation e, RedirectAttributes flushAttrs) throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.insert(request, e, flushAttrs);	}	@RequestMapping(value = "update", method = RequestMethod.POST)	@Override	public String update(HttpServletRequest request, Navigation e, RedirectAttributes flushAttrs) throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.update(request, e, flushAttrs);	}}