package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import static com.zlkj.ssm.shop.common.basic.Base.empty;import com.zlkj.ssm.shop.core.ManageContainer;import com.zlkj.ssm.shop.manage.entity.Sms;import com.zlkj.ssm.shop.manage.service.AdminSmsService;import com.zlkj.ssm.shop.sms.SMSWebChinese;import com.zlkj.ssm.shop.web.controller.BaseController;import com.zlkj.ssm.shop.web.tools.RequestHolder;/** * 短信管理 */@Controller@RequestMapping("sms")public class AdminSmsController extends BaseController<Sms> {	@Autowired	private AdminSmsService smsService;	public AdminSmsService getService() {		return smsService;	}	/**	 * 重发短信	 * @return	 * @throws Exception	 */	@RequestMapping("updateSendSMS")	public String updateSendSMS(Sms e) throws Exception{		if(empty(e.getId())){			throw new NullPointerException(ManageContainer.RoleAction_update_error);		}				e = smsService.selectById(e.getId());		if(e==null){			throw new NullPointerException("系统查询不到此短信！");		}				SMSWebChinese.sendSMS(e);		Sms sms = new Sms();		sms.setId(e.getId());		sms.setReturnCode(e.getReturnCode());		smsService.update(e);				return selectList(RequestHolder.getRequest(), e);	}}