package com.zlkj.ssm.shop.web.controller.manage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import com.zlkj.ssm.shop.core.page.PagerModel;import com.zlkj.ssm.shop.manage.entity.Systemlog;import com.zlkj.ssm.shop.manage.service.AdminSystemlogService;import com.zlkj.ssm.shop.web.controller.BaseController;import com.zlkj.ssm.shop.web.tools.RequestHolder;/** * 系统日志管理 */@Controller@RequestMapping("/manage/systemlog/")public class AdminSystemlogController extends BaseController<Systemlog> {    @Autowired	private AdminSystemlogService systemlogService;    private static final String page_toList = "/manage/systemlog/systemlogList";    private static final String page_toAdd = "/manage/systemlog/systemlogEdit";    private static final String page_toEdit = "/manage/systemlog/systemlogEdit";    private static final String page_systemlogListAndDetail = "/manage/systemlog/systemlogListAndDetail";    public AdminSystemlogController() {        super.page_toList = page_toList;        super.page_toEdit = page_toAdd;        super.page_toAdd = page_toEdit;    }    @Override	public AdminSystemlogService getService() {		return systemlogService;	}    @Override	protected void selectListAfter(PagerModel pager) {		pager.setPagerUrl("selectList");	}	public void setSystemlogService(AdminSystemlogService systemlogService) {		this.systemlogService = systemlogService;	}    @Override	public void insertAfter(Systemlog e) {		e.clear();	}	@RequestMapping("systemlogListAndDetail")	public String systemlogListAndDetail(@RequestParam(required = false)Integer type, Systemlog e) throws Exception{		if(type == null){			e.setType(Integer.valueOf(type));		}		selectList(RequestHolder.getRequest(), e);		return page_systemlogListAndDetail;	}}